def fibonacci_sequence(n): 
    # set initial values 
    a = 0 
    b = 1  
    if n == 0: 
        return a 
    elif n == 1:
        return b 
    else: 
        # loop over the sequence 
        for i in range(2,n): 
            c = a + b 
            a = b 
            b = c 
        return b 

